import React,{useEffect,useState} from 'react'


const Profile = ()=>{
    const[mypics,setPics] = useState([])
    useEffect(()=>{
    fetch('/mypost',{
        heders:{
        "Authorization":"Bearer"+localStorage.getItem("jwt")
    }
    }).then(res=>res.jason())
    .then(result=>{
        setPics(result.mypost)
    })
    },[])
    return(
        <div style={{maxWidth:"550px",margin:"0px auto"}}>
            <div style={{display:"flex",
                  justifyContent:"space-around",
                  margin:"18px 0px",
                  borderBottom:"1px solid black"}}>
                <div>
                    <img style={{width:"160px",height:"160px",borderRadius:"80px"}}
                    src="https://media.istockphoto.com/photos/young-man-using-smart-phone-at-home-picture-id1278009593?b=1&k=20&m=1278009593&s=170667a&w=0&h=uCTPNkqE--LHcrjm14dtmg_NeOjLqi3_9hQfeM9xrVg="/>
                </div>
                <div>
                    <h4>Ramesh varma</h4>
                    <div style={{display:"flex",justifyContent:"space-between",width:"108%"}}>
                        <h5>40 post</h5>
                        <h5>40 followers </h5>
                        <h5>40 folowing  </h5>
                        </div>
                    

                </div>
            </div>
            <div className="gallery">
                {
                    mypics.map(itwm=>{
                        return(
                            <img className="item" src={item.photo} alt={item.title}/>
                        )
                    })
                }
                   
                    

            </div>

        </div>


    )
}

export default Profile
