import React, { Component,useState } from 'react';
import {Link,useHistory} from 'react-router-dom'
import M from 'materialize-css';

const CreatePost = ()=>{
    const history=useHistory();
    const [name,setName]=useState("");
  const [email,setEmail]=useState("");
  const [password,setpassword]=useState("");
  const PostData=()=>
  {
    if(!/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(email))
    {
         {M.toast({html: "Invalied Email", classes: 'rounded',classes:'#f44336 red'});}
    }
    fetch("/signup",
    {
        method:"post",
        headers:{
            "Content-Type":"application/json"
        },
        body:JSON.stringify({
            name,
            email,
            password
        })
    }).then(res=>res.json()).then(data=>{
          if(data.error)
            {M.toast({html: data.error, classes: 'rounded',classes:'#f44336 red'});}
        else
        {
            M.toast({html: data.message, classes: 'rounded',classes:'#388e3c green darken-2'});
            history.push('/Login');
        }
    })
  }
    return(
    <div className="card input-filed"
    style={{
        margin:"30px auto",
        maxWidth:"500px",
        padding:"20px",
        textAlign:"center"}} >
        <input type="text" placeholder="titel"/>
        <input type="text" placeholder="Body"/>
        <div className="file-field input-field">
            <div className="btn #64b5f6 blue darken-1">
                <span>Upload Image</span>
                <input type="file"/>
            </div>
            <div className="file-path-wrapper">
                <input className="file-path validate" type="text"/>
            </div> 
              
    </div>
    <button className="btn waves-effect waves-light #64b5f6 blue darken-1 " >Submit post</button>



    </div>
    )




}
export default CreatePost
