import React,{useState,useEffect,useContext} from 'react'




const Home = ()=>{ 
    const [data,setData] = useState([])
   
    useEffect(()=>{
        fetch('/allpost',{
            headers:{
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            }
        }).then(res=>res.json())
        .then(result=>{
            console.log(result)
            setData(result.posts)
        })
     },[])
    const likePost=(id)=>{
        fetch('/like',{
            method:"put",
            headers:{
                "Content-Type":"application/json",
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            },
            body:JSON.stringify({
                postId:id
            })
        }).then(res=>res.json())
        .then(result=>
        {

           const newData=data.map(item=>
            {
                if(item._id==result._id){
                    return result
                }else
                {
                    return item
                }
            })
            setData(newData)
        })
    }
    const unlikePost=(id)=>{
        fetch('/unlike',{
            method:"put",
            headers:{
                "Content-Type":"application/json",
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            },
            body:JSON.stringify({
                postId:id
            })
        }).then(res=>res.json())
        .then(result=>
        {
            const newData=data.map(item=>
            {
                if(item._id==result._id){
                    return result
                }else
                {
                    return item
                }
            })
            setData(newData)
        })
    }
    return(
        <div className="home" >
            {
                data.map(item=>{
                    return(
                        <div className="card home-card" key={item._id}>
                        <h5>{item.postedBy.name}</h5>
                        <div className="card-image">
                            <img src={item.photo}/>
        
                        </div>
                        <div className="card-content">
                        {item.likes.includes(item._id)?
                           <i className="material-icons" onClick={()=>{unlikePost(item._id)}}>thumb_down</i>
                           : 
                           <i className="material-icons" onClick={()=>{likePost(item._id)}}>thumb_up</i>
                        }
                        <h6>{item.likes.includes(item._id)}</h6>
                        <i className="material-icons" style={{color:"red"}}>favorite</i>
                        <i className="material-icons" onClick={()=>{unlikePost(item._id)}}>thumb_down</i>
                        
                            <h6>{item.likes.length} Likes</h6>
                            <h6>{item.title}</h6>
                            <p>{item.body}</p>
                            <input type="text" placeholder="Add comment"/>
                            
        
                        </div>
                    </div>
                    )
                })
            }
            <div className="card home-card">
                <h5>Ramesh</h5>
                <div className="card-image">
                    <img src="https://media.istockphoto.com/photos/colorful-background-picture-id1280385511?b=1&k=20&m=1280385511&s=170667a&w=0&h=4-KMkIqgyw2gTBMTBbYZVZoidwRsWZzr3q0xyUDYhas="/>

                </div>
                <div className="card-content">
                <i className="material-icons" style={{color:"red"}}>favorite</i>
                    <h6>titel</h6>
                    <input type="text" placeholder="Add comment"/>
                    

                </div>
            </div>
           


        </div>

    )
}

export default Home
