import './App.css';
import './components/navbar'
import React, { Component,useState,useEffect,createContext,useReducer } from 'react';
import NavBar from './components/navbar';
import {BrowserRouter,Route,useHistory} from 'react-router-dom'
import Home from './components/screens/Home'
import Profile from './components/screens/Profile'
import Signin from './components/screens/Login'
import Signup from './components/screens/Signup'
import CreatePost from './components/screens/CreatePost'
import Front from './components/screens/Front'
import  {initialSatate,reducer} from  './reducer/userreducer'

const App = ()=> {
  <script src="https://cdnjs.cloudflare.com/ajax/libs/materialize/1.0.0/js/materialize.min.js"></script>
  React.useLayoutEffect(() => {
    var [isNavbarHidden, setIsNavbarHidden] = useState(false);
   navigation.setOptions({headerShown: false});
  }, [navigation]); 
        return (<div>
           <div id="front">
           <div>Social Media Management</div>
        </div>
       <BrowserRouter>
    <NavBar />
    <Route path='/Login'  render={() => <Signin  setNavbar={setIsNavbarHidden}  />}/>
    <Route path='/Home'>
      <Home/>
      </Route>
      <Route path='/Signup'>
      <Signup/>
      </Route>
      <Route path='/Profile'>
      <Profile/>
      </Route>
      <Route path='/CreatePost'>
      <CreatePost/>
      </Route>
      <Route path='/Front'>
      <Front/>
      </Route>
    </BrowserRouter>
    </div>
        );
    };

export default App;
